import React, { memo, useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { Room as RoomType } from '../../../components';
import { useRequest } from '../../../utils';
import { Image, Divider, Tag, Grid, Typography } from 'antd';
import style from './index.module.scss';
import { baseURL } from '../../../config';

const { useBreakpoint } = Grid;
const { Title, Text } = Typography;

// 分割标签 - eg. 配套齐全/精装修/拎包入住
function splitTags(tags: string) {
	return tags.split('/');
}
// 获取图片的宽度
const useImgWidth = () => {
	const screens = useBreakpoint(); // 响应式布局
	let width = '50%';
	if (screens.xs === false) {
		width = '30%';
	}
	if (screens.xl === false) {
		width = '50%';
	}
	if (screens.xs) {
		width = '90%';
	}
	return width;
};

interface IPros {}
const defaultProps = {};
const RoomComponent: React.FC<IPros> = (props) => {
	const params = useParams<{ id: string }>();
	const { state } = useLocation<{ room: RoomType }>(); // 从首页点击传递过来的参数
	const width = useImgWidth();
	const [room, setRoom] = useState<RoomType | undefined>(() => {
		if (!state) return undefined;
		else return state.room;
	});
	/**
	 * 请求数据
	 */
	const { loading, run } = useRequest<
		{
			type: 'success' | 'error';
			msg: string;
		},
		{}
	>(`/home/room/${params.id}`, {
		onSuccess: ({ data, params }) => {
			console.info(JSON.parse(data.msg));
			setRoom(JSON.parse(data.msg));
		},
	});
	useEffect(() => {
		// 如果传递过来的state为空，则请求数据
		if (!room) {
			run({});
		}
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [state]);

	return (
		<div className={style.container}>
			{loading || !room ? (
				<span>loading</span>
			) : (
				<>
					<div className={style.img}>
						<Image
							width={width}
							src={`${baseURL}/images/${room.url}.jpg`}
							fallback='data:image/png;base64,'
						/>
					</div>
					<div className={style.header}>
						<Title level={5}>{room?.name}</Title>
						<Text className={style.price} type='danger' strong>
							{room?.price}元/月
						</Text>
					</div>
					<Divider />
					<div className={style.infoContainer}>
						<div>
							<Title level={4} type='danger'>
								{room?.housetype}
							</Title>
							<Text type='secondary'>户型</Text>
						</div>
						<div>
							<Title level={4} type='danger'>
								{room?.area}m<sup>2</sup>
							</Title>
							<Text type='secondary'>面积</Text>
						</div>
						<div>
							<Title level={4} type='danger'>
								{room?.dir}
							</Title>
							<Text type='secondary'>朝向</Text>
						</div>
					</div>
					<Divider />
					<div className={style.tags}>
						{splitTags(room?.tags as string).map((tag, index) => (
							<Tag key={index} color='blue'>
								{tag}
							</Tag>
						))}
					</div>
				</>
			)}
		</div>
	);
};
RoomComponent.defaultProps = defaultProps;
export default memo(RoomComponent);
